CREATE INDEX "IX_Employees_genderId" ON "Employees" ("genderId");

CREATE INDEX "IX_Employees_RoleAssignId" ON "Employees" ("RoleAssignId");

CREATE INDEX "IX_LeaveAllocations_financialYearId" ON "LeaveAllocations" ("financialYearId");

