CREATE INDEX "IX_UserRoleMapping_ApplicationPageId" ON "UserRoleMapping" ("ApplicationPageId");

CREATE INDEX "IX_UserRoleMapping_RoleAssignId" ON "UserRoleMapping" ("RoleAssignId");

